function setup() {
  createCanvas(640, 300);

  a_mic = new p5.AudioIn();
  a_mic.start();

  a_fft = new p5.FFT();
  a_fft.setInput(a_mic);

  n_vol = int(width / vol_len);
  console.log('n_vol', n_vol)
  createSpan().id('vol')
}

