    resizeCanvas(i_width, i_height);
    init_setup();
  });
  createButton('HD').mousePressed(function() {
    resizeCanvas(1920, 1080);
    init_setup();
  });
  createButton('Reset').mousePressed(function() {
    init_setup();
  });
}

