// https://editor.p5js.org/jht1900/sketches/-D9xJeXAT
// motion-2-circle class

let objs = [];
let i_scale = 1;
let i_width = 640 * i_scale;
let i_height = 360 * i_scale;
let a_alpha = 2;
let n_splat = 100;  // How many to obj to draw in a frame

function setup() {
  createCanvas(i_width, i_height).mousePressed(function () {
    background(255);
  });
  noStroke();
  background(255);
  init_setup();
  create_ui();
  // frameRate(0.5);
}

function draw() {
  for (let index = 0; index < n_splat; index++) {
    for (let ob of objs) {
      ob.draw();
    }
  }
}

function init_setup() {
  let obj1 = {
    step: 0.01, // each step (0.0 to 1.0)
    pct: 0, // Percentage traveled (0.0 to 1.0)
    gray: 0,
    x: 320,
    y: 180,
    d: 100,
    d_range: [50, 100],
    color_step: 1,
    alpha: a_alpha,
  };
  let obj2 = {
    step: 0.01, // each step (0.0 to 1.0)
    pct: 0, // Percentage traveled (0.0 to 1.0)
    gray: 255,
    x: 320,
    y: 180,
    d: 10,
    d_range: [10, 20],
    color_step: -1,
    alpha: a_alpha,
  };
  objs = [];
  objs.push(new Splat(obj1));
  objs.push(new Splat(obj2));
}

class Splat {
  constructor(props) {
    Object.assign(this, props);
    this.initpos();
  }
  initpos() {
    this.step = (1 / width) * 7;
