var ball = {
  x: 300,
  y: 200,
  xspeed: 0,
  yspeed: 0
};

function setup() {
  createCanvas(600, 400);
}

function draw() {
  // background(0);
  move();
  bounce();
  display();
}

function bounce() {
  if (ball.x > width || ball.x < 0) {
    ball.xspeed = ball.xspeed * -1;
  }
  if (ball.y > height || ball.y < 0) {
    ball.yspeed = ball.yspeed * -1;
  }
}

function display() {
  stroke(255);
  strokeWeight(4);
  fill(200, 0, 200);
  ellipse(ball.x, ball.y, 24, 24);
}

function move() {
  ball.x = ball.x + ball.xspeed;
  ball.y = ball.y + ball.yspeed;
}

function mousePressed() {
  if (ball.xspeed == 0) {
    ball.xspeed = 10;
    ball.yspeed = 0;
  } 
  else {
    ball.xspeed = 0;
    ball.yspeed = 10;
  }
}

