// https://editor.p5js.org/jht1900/sketches/-Ypn6ODK_
// video scan radial

let nwidth = 640;
let nheight = 480;
let xgap = nwidth / 6;
let ygap = nheight / 6;
let x0;
let y0;
let capture;
let ang = 0;
let astep = 1;

function setup() {
  createCanvas(nwidth, nheight);
  capture = createCapture(VIDEO);
  capture.size(width, height);
  capture.hide();
  x0 = int(nwidth / 2);
  y0 = int(nheight / 2);
}

function draw() {
  // colorMode(HSB);
  strokeWeight(1);
  // background(255);

  // image(img, dx, dy, dWidth, dHeight, sx, sy, [sWidth], [sHeight],
  let img = capture.get();
  // image(img, 0, 0, nwidth);

  let r = xgap/2;
  let rang = radians(ang)
  let x1 = r * cos(rang);
  let y1 = r * sin(rang);

  let c1 = img.get(x0+x1, y0+y1);
  stroke(c1);
  fill(c1)
  circle(x0+x1,y0+y1, 10);

  r = nwidth;
  let x2 = r * cos(rang);
  let y2 = r * sin(rang);
  line(x0+x1, y0+y1, x0+x2, y0+y2);

  ang = (ang + astep);
}

function draw_gap() {
  let y1 = y0 - ygap/2;
  let y2 = y0 + ygap/2;
  let x1 = x0 - xgap/2;
  let x2 = x0 + xgap/2;
  for (let x = 0; x < img.width; x++) {
    let c1 = img.get(x, y1);
    stroke(c1);
    line(x, 0, x, y1);
    let c2 = img.get(x, y2);
    stroke(c2);
    line(x, y2, x, nheight);
  }
  for (let y = y1; y < y2; y++) {
    let c1 = img.get(x1, y);
    stroke(c1);
    line(0, y, x1, y);
    let c2 = img.get(x2, y);
    stroke(c2);
    line(x2, y, nwidth, y);
  }
}

